import React, { useRef, useEffect } from 'react'
import Map from '@arcgis/core/Map'

import buildings from '../untils/SceneLayerConfig'
import sceneView from '../untils/SceneViewConfig'

export default function MapView() {
    const mapRef = useRef()

    useEffect(() => {
        const map = new Map({
            basemap: 'satellite',
            layers: [buildings({ url: "https://services.arcgis.com/V6ZHFr6zdgNZuVG0/ArcGIS/rest/services/NYC/SceneServer/layers/0" })]
        })

        const view = sceneView({ ref: mapRef.current, map })

        view.when().then(async () => {
            const buildingsLV = await view.whenLayerView(buildings);
            let highlight = null;
            view.on(
                "pointer-move",
                promiseUtils.debounce(async (e) => {
                    const ht = await view.hitTest(e, {
                        include: [buildings]
                    });
                    if (highlight) {
                        highlight.remove();
                        highlight = null;
                    }
                    if (ht.results.length > 0) {
                        // select the first feature (closest to the camera)
                        const graphic = ht.results[0].graphic;
                        if (graphic) {
                            highlight = buildingsLV.highlight(graphic);
                        }
                    }
                })
            );
            view.on("pointer-leave", () => {
                if (highlight) {
                    highlight.remove();
                    highlight = null;
                }
            });
        });

        return () => { view && view.destroy() }
    }, [])

    return (
        <div ref={mapRef} style={{ height: '100vh' }} />
    )
}
