import React, { useRef, useEffect } from 'react'
import ArcGISMap from '../untils/ArcGISMap'

export default function MapView() {
    const mapRef = useRef()

    useEffect(() => {
        const view = ArcGISMap("https://services.arcgis.com/V6ZHFr6zdgNZuVG0/ArcGIS/rest/services/NYC/SceneServer/layers/0", 'topo-vector', mapRef.current)

        return () => { view && view.destroy() }
    }, [])

    return (
        <div ref={mapRef} style={{ height: '100vh' }} />
    )
}
