import Map from '@arcgis/core/Map'
import Color from "@arcgis/core/Color"
import SceneView from "@arcgis/core/views/SceneView"
import SceneLayer from '@arcgis/core/layers/SceneLayer'
import MeshSymbol3D from '@arcgis/core/symbols/MeshSymbol3D'
import SolidEdges3D from '@arcgis/core/symbols/edges/SolidEdges3D'
import SimpleRenderer from '@arcgis/core/renderers/SimpleRenderer'
import FillSymbol3DLayer from '@arcgis/core/symbols/FillSymbol3DLayer'

import GraphicsLayer from '@arcgis/core/layers/GraphicsLayer'

import * as promiseUtils from '@arcgis/core/core/promiseUtils'

const ArcGISMap = (url, basemap, ref) => {
    const buildings = new SceneLayer({
        url,
        opacity: 1,
        popupEnabled: false,
        renderer: new SimpleRenderer({
            symbol: new MeshSymbol3D({
                symbolLayers: [
                    new FillSymbol3DLayer({
                        material: {
                            color: new Color([200, 200, 200]),
                            colorMixMode: "replace"
                        },
                        edges: new SolidEdges3D({
                            color: new Color([100, 100, 100, 0.5])
                        })
                    })
                ]
            })
        })
    });

    const walls = new GraphicsLayer({
        elevationInfo: {
            mode: "absolute-height"
        }
    });

    const map = new Map({
        basemap,
        layers: [buildings, walls]
    });

    const view = new SceneView({
        container: ref,
        map,
        qualityProfile: "high",
        camera: {
            position: [-73.98564294432742, 40.748586782824624, 601.67648],
            heading: 330.47,
            tilt: 64.02
        },
        environment: {
            lighting: {
                directShadowsEnabled: true
            }
        },
    })

    view.when().then(async () => {
        const buildingsLV = await view.whenLayerView(buildings);
        let highlight = null;
        view.on(
            "pointer-move",
            promiseUtils.debounce(async (e) => {
                const ht = await view.hitTest(e, {
                    include: [buildings]
                });
                if (highlight) {
                    highlight.remove();
                    highlight = null;
                }
                if (ht.results.length > 0) {
                    // select the first feature (closest to the camera)
                    const graphic = ht.results[0].graphic;
                    if (graphic) {
                        highlight = buildingsLV.highlight(graphic);
                    }
                }
            })
        );
        view.on("pointer-leave", () => {
            if (highlight) {
                highlight.remove();
                highlight = null;
            }
        });
    });

    return view
}

export default ArcGISMap
