import React, { useRef, useEffect, useState } from 'react'
import Map from '@arcgis/core/Map'
import Color from "@arcgis/core/Color"
import SceneView from "@arcgis/core/views/SceneView"
import SceneLayer from '@arcgis/core/layers/SceneLayer'
import MeshSymbol3D from '@arcgis/core/symbols/MeshSymbol3D'
import SolidEdges3D from '@arcgis/core/symbols/edges/SolidEdges3D'
import SimpleRenderer from '@arcgis/core/renderers/SimpleRenderer'
import FillSymbol3DLayer from '@arcgis/core/symbols/FillSymbol3DLayer'

import Buildings from '../untils/SceneLayerConfig'

export default function MapView() {
    const mapRef = useRef()
    const [layerURL, setLayerURL] = useState("https://services.arcgis.com/V6ZHFr6zdgNZuVG0/ArcGIS/rest/services/NYC/SceneServer/layers/0")

    useEffect(() => {
        // const buildings = new SceneLayer({
        //     opacity: 1,
        //     popupEnabled: false,
        //     renderer: new SimpleRenderer({
        //         symbol: new MeshSymbol3D({
        //             symbolLayers: [
        //                 new FillSymbol3DLayer({
        //                     material: {
        //                         color: new Color([200, 200, 200]),
        //                         colorMixMode: "replace"
        //                     },
        //                     edges: new SolidEdges3D({
        //                         color: new Color([100, 100, 100, 0.5])
        //                     })
        //                 })
        //             ]
        //         })
        //     }),
        //     url: "https://services.arcgis.com/V6ZHFr6zdgNZuVG0/ArcGIS/rest/services/NYC/SceneServer/layers/0"
        // })

        const map = new Map({
            basemap: 'topo-vector',
            layers: [Buildings]
        })

        const view = new SceneView({
            container: mapRef.current,
            map,
            qualityProfile: "high",
            camera: {
                position: [-73.99756689528948, 40.724214693182205, 601.67648],
                heading: 330.47,
                tilt: 64.02
            },
            highlightOptions: {
                color: new Color([255, 153, 0]),
                fillOpacity: 0.6,
                haloOpacity: 0
            }
        });

        return () => { view && view.destroy() }
    }, [])

    return (
        <div ref={mapRef} style={{ height: '100vh' }} />
    )
}
