import React, { useRef, useEffect, useState } from 'react'
import ArcGISMap from '../untils/ArcGISMap'

export default function MapView({ url, basemap, onClick }) {
    const mapRef = useRef()
    const [view, setView] = useState(null)

    useEffect(() => {
        setView(ArcGISMap(url, basemap, mapRef.current));

        return () => { view && view.destroy() }
    }, [])

    useEffect(() => {
        if (!view) return
        view.map.basemap = basemap
    }, [view, basemap])

    useEffect(() => {
        if (!view) return
        const handle = view.when().then(async () => {
            const buildingsLV = await view.whenLayerView(buildings);
            let highlight = null;
            view.on(
                "pointer-move",
                promiseUtils.debounce(async (e) => {
                    const ht = await view.hitTest(e, {
                        include: [buildings]
                    });
                    if (highlight) {
                        highlight.remove();
                        highlight = null;
                    }
                    if (ht.results.length > 0) {
                        // select the first feature (closest to the camera)
                        const graphic = ht.results[0].graphic;
                        if (graphic) {
                            highlight = buildingsLV.highlight(graphic);
                        }
                    }
                })
            );
            view.on("pointer-leave", () => {
                if (highlight) {
                    highlight.remove();
                    highlight = null;
                }
            });
        });
        return () => { handle && handle.remove() }
    }, [view, onClick])

    return (
        <div ref={mapRef} style={{ height: '100vh' }} />
    )
}
